import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  IconButton, 
  Button, 
  Card,
  CardContent,
  Grid,
  Box,
  CardMedia,
  CircularProgress
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login to view cart');
        setLoading(false);
        return;
      }

      const response = await axios.get('http://localhost:5000/cart', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      console.log('Cart response:', response.data);
      
      if (response.data && response.data.items) {
        setCartItems(response.data.items);
        calculateTotal(response.data.items);
      }
      
      setLoading(false);
    } catch (error) {
      console.error('Error fetching cart:', error);
      setError(error.response?.data?.message || 'Error loading cart');
      setLoading(false);
    }
  };

  const calculateTotal = (items) => {
    const sum = items.reduce((acc, cartItem) => {
      if (cartItem.item && cartItem.item.price) {
        return acc + (cartItem.item.price * cartItem.quantity);
      }
      return acc;
    }, 0);
    setTotal(sum);
  };

  const handleRemoveItem = async (itemId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/cart/${itemId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
    } catch (error) {
      console.error('Error removing item:', error);
      alert('Error removing item from cart');
    }
  };

  if (loading) return (
    <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
      <CircularProgress />
    </Box>
  );

  if (error) return (
    <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
      <Typography color="error">{error}</Typography>
    </Box>
  );

  return (
    <Container>
      <Typography variant="h4" gutterBottom sx={{ my: 4 }}>
        Shopping Cart
      </Typography>
      
      {cartItems.length === 0 ? (
        <Typography>Your cart is empty</Typography>
      ) : (
        <Grid container spacing={3}>
          <Grid item xs={12}>
            {cartItems.map((cartItem) => {
              if (!cartItem.item) return null;
              
              return (
                <Card key={cartItem._id} sx={{ mb: 2 }}>
                  <CardContent>
                    <Grid container alignItems="center" spacing={2}>
                      <Grid item xs={12} sm={2}>
                        {cartItem.item.image && (
                          <CardMedia
                            component="img"
                            height="100"
                            image={cartItem.item.image}
                            alt={cartItem.item.name}
                            sx={{ objectFit: 'cover', borderRadius: 1 }}
                          />
                        )}
                      </Grid>
                      <Grid item xs={12} sm={4}>
                        <Typography variant="h6">
                          {cartItem.item.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          Category: {cartItem.item.category}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          Quantity: {cartItem.quantity}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          Price: ₹{cartItem.item.price.toFixed(2)}
                        </Typography>
                      </Grid>
                      <Grid item xs={12} sm={4}>
                        <Typography variant="h6">
                          Subtotal: ₹{(cartItem.item.price * cartItem.quantity).toFixed(2)}
                        </Typography>
                      </Grid>
                      <Grid item xs={12} sm={2}>
                        <Box display="flex" justifyContent="flex-end">
                          <IconButton 
                            onClick={() => handleRemoveItem(cartItem._id)}
                            color="error"
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Box>
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              );
            })}
          </Grid>
          
          <Grid item xs={12}>
            <Card>
              <CardContent>
                <Typography variant="h5" gutterBottom>
                  Cart Summary
                </Typography>
                <Typography variant="h6">
                  Total: ₹{total.toFixed(2)}
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  fullWidth
                  sx={{ mt: 2 }}
                  onClick={() => alert('Checkout functionality coming soon!')}
                >
                  Proceed to Checkout
                </Button>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      )}
    </Container>
  );
};

export default Cart;
